package com.tianzhen.android.test.paper.home;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.tianzhen.android.test.paper.Constants;import com.tianzhen.android.test.paper.R;import com.tianzhen.android.test.paper.entity.HomeBean;import com.tianzhen.android.test.paper.shake.ShakeActivity;import com.tianzhen.android.test.paper.util.Tools;import com.tianzhen.android.test.paper.view.AutoScrollViewPager;import com.tianzhen.android.test.paper.view.BaseViewPagerAdapter;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;import butterknife.Unbinder;/** * Created by Administrator on 2017/9/21 0021. */public class HomeFragment extends Fragment implements HomeContract.View, BaseViewPagerAdapter.OnAutoViewPagerItemClickListener {    Unbinder unbinder;    @BindView(R.id.btn_company)    Button btnCompany;    @BindView(R.id.btn_person)    Button btnPerson;    @BindView(R.id.btn_product)    Button btnProduct;    @BindView(R.id.btn_sdi)    Button btnSdi;    @BindView(R.id.tv_headline1)    TextView tvHeadline1;    @BindView(R.id.tv_headline2)    TextView tvHeadline2;    @BindView(R.id.viewPager1)    AutoScrollViewPager viewPager;    @BindView(R.id.viewPager2)    AutoScrollViewPager viewPager2;    @BindView(R.id.recycler_sd)    RecyclerView recyclerSd;    private HomePresenter homePresenter;    private BaseViewPagerAdapter autoAdapter;    private List<String> imageList;    private String tips;    public static HomeFragment newInstance(String tip) {        Bundle bundle = new Bundle();        bundle.putString("tip", tip);        HomeFragment homeFragment = new HomeFragment();        homeFragment.setArguments(bundle);        return homeFragment;    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        View root = inflater.inflate(R.layout.frg_home, container, false);        setHasOptionsMenu(true);        unbinder = ButterKnife.bind(this, root);        return root;    }    @Override    public void onResume() {        super.onResume();        homePresenter.getData();    }    @Override    public void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        homePresenter = new HomePresenter(this);    }    @Override    public void onDestroyView() {        super.onDestroyView();    }    @Override    public void onItemClick(int position, Object o) {        Tools.showToast(getContext(), o.toString());    }    @Override    public void bannerClick() {    }    @Override    public void loadBanner1(final List<HomeBean.DATABean.FirstImgBean> firstImgBeen) {        autoAdapter = new BaseViewPagerAdapter(getContext(), firstImgBeen, this) {            @Override            public void loadImage(ImageView view, int position, Object o) {                Glide.with(getContext()).load(Constants.webURL + firstImgBeen.get(position).getQTSCONTENTIMAGE()).into(view);            }        };        viewPager.setAdapter(autoAdapter);    }    @Override    public void loadBanner2(final List<HomeBean.DATABean.SecondImgBean> list) {        viewPager2.setAdapter(new BaseViewPagerAdapter(getContext(), list, this) {            @Override            public void loadImage(ImageView view, int position, Object o) {                Glide.with(getContext()).load(Constants.webURL + list.get(position).getQTSCONTENTIMAGE()).into(view);            }        });    }    @Override    public void loadHeadlines(List<HomeBean.DATABean.NewsListBean> list) {        tvHeadline1.setText(list.get(0).getQTSCONTENTTITLE());        tvHeadline2.setText(list.get(1).getQTSCONTENTTITLE());    }    @Override    public void loadSDInfor(HomeBean homeBean) {    }    @Override    public void setPresenter(HomeContract.Presenter presenter) {    }    @Override    public void showToast(String title) {    }    @Override    public void showResult(String result) {    }    @OnClick({R.id.btn_company, R.id.btn_person, R.id.btn_product, R.id.btn_sdi})    public void onViewClicked(View view) {        switch (view.getId()) {            case R.id.btn_company:                startActivity(new Intent(getActivity(), ShakeActivity.class));                break;            case R.id.btn_person:                break;            case R.id.btn_product:                break;            case R.id.btn_sdi:                break;            default:                break;        }    }}